$sql[] = "CREATE TABLE " . $wpdb->prefix . "ds_classes (\n  id bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,\n  name varchar(250) NOT NULL,\n  class_fee float(5,2) NOT NULL,\n  costume_fee float(5,2) NOT NULL DEFAULT '65.00',\n  category_id bigint(20) NOT NULL,\n  woocommerce_id bigint(20) NOT NULL,\n  classroom varchar(250) NOT NULL,\n  ages varchar(100) NOT NULL,\n  experience int(3) NOT NULL DEFAULT '0',\n  description text,\n  startdate date NOT NULL,\n  enddate date NOT NULL,\n  days varchar(50) NOT NULL,\n  starttime time NOT NULL,\n  endtime time NOT NULL,\n  is_parent_event int(11) NOT NULL,\n  parent_id int(10) UNSIGNED DEFAULT NULL,\n  is_competitive tinyint(4) NOT NULL DEFAULT '0',\n  is_registerable tinyint(1) DEFAULT NULL,\n  use_global_installments tinyint(1) UNSIGNED DEFAULT '0' NOT NULL,\n  sessions_id smallint(5) UNSIGNED DEFAULT NULL,\n  PRIMARY KEY  (id),\n  KEY parent_id (parent_id),\n  KEY sessions_id (sessions_id),\n  KEY woocommerce_id (woocommerce_id)\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;";
