$sql[] = "CREATE TABLE " . $wpdb->prefix . "ds_students (\n  id bigint(20) NOT NULL AUTO_INCREMENT,\n  firstname varchar(100) NOT NULL,\n  lastname varchar(100) NOT NULL,\n  parent_id bigint(20) NOT NULL,\n  parent2_id int(11) NOT NULL,\n  birthdate date DEFAULT NULL,\n  user_level varchar(25) DEFAULT NULL,\n  gender enum('male','female') NOT NULL,\n  address_data text NOT NULL,\n  measurements text,\n  meta text NOT NULL,\n  date_added timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,\n  is_confirmed tinyint(4) NOT NULL DEFAULT '0',\n  active tinyint(4) NOT NULL DEFAULT '0',\n  can_register_online tinyint(4) NOT NULL DEFAULT '1',\n  schedule_available tinyint(4) NOT NULL DEFAULT '0',\n  sessions_id smallint(5) UNSIGNED DEFAULT NULL,\n  PRIMARY KEY  (id),\n  KEY firstname (firstname),\n  KEY lastname (lastname),\n  KEY parent_id (parent_id),\n  KEY parent2_id (parent2_id),\n  KEY is_confirmed (is_confirmed),\n  KEY sessions_id (sessions_id)\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;";
$sql[] = "CREATE TABLE " . $wpdb->prefix . "ds_students_history (\n  id int(10) UNSIGNED NOT NULL AUTO_INCREMENT,\n  student_id int(10) UNSIGNED NOT NULL,\n  type varchar(255) NOT NULL,\n  value text NOT NULL,\n  value2 text NOT NULL,\n  sessions_id smallint(5) UNSIGNED DEFAULT NULL,\n  PRIMARY KEY  (id)\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;";
$sql[] = "CREATE TABLE " . $wpdb->prefix . "ds_venues (\n  id bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,\n  name varchar(255) NOT NULL,\n  address_data text,\n  meta text,\n  PRIMARY KEY  (id),\n  UNIQUE KEY name (name)\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;";
$sql[] = "CREATE TABLE " . $wpdb->prefix . "ds_countries (\n   id int(11) NOT NULL AUTO_INCREMENT,\n   code varchar(2) NOT NULL,\n   name varchar(100) NOT NULL,\n   PRIMARY KEY  (id),\n   UNIQUE KEY code (code)\n) ENGINE=InnoDB DEFAULT CHARSET=latin1;";